CREATE TABLE IF NOT EXISTS "Users" ('ID' INTEGER PRIMARY KEY AUTOINCREMENT, 'SlackID' TEXT NOT NULL DEFAULT 'NOID', 'UserName' TEXT NOT NULL DEFAULT 'NONAME');
CREATE TABLE IF NOT EXISTS "Issues" ('ID' INTEGER PRIMARY KEY AUTOINCREMENT, 'TimeStamp' DATE DEFAULT (datetime('now','localtime')), 'ServerNumber' INTEGER NOT NULL, 'ServerStatus' TEXT NOT NULL, 'SlackID' TEXT NOT NULL DEFAULT 'NONAME');
CREATE TABLE IF NOT EXISTS "Music" ('ID' INTEGER PRIMARY KEY AUTOINCREMENT, 'Link' TEXT NOT NULL DEFAULT 'www.google.com', 'Description' TEXT NOT NULL DEFAULT 'NOT SET');
CREATE TABLE IF NOT EXISTS "Interactions" ('ID' INTEGER PRIMARY KEY AUTOINCREMENT, 'TimeStamp' DATE DEFAULT (datetime('now','localtime')), 'User' TEXT NOT NULL DEFAULT 'NONE', 'aStatus' TEXT NOT NULL DEFAULT 'NONE');
